INSERT INTO ai_intents (tenant_id, name, description, keywords, response_template, follow_up_action, is_active) VALUES 
(1, 'Agendamento de Consulta', 'Cliente quer agendar uma consulta', 'agendar,consulta,horario,marcar,appointment', 'Olá! Ficamos felizes que você queira agendar uma consulta conosco! 😊 Para encontrarmos o melhor horário, me conta: qual seria o melhor período para você? Manhã, tarde ou noite?', 'schedule_appointment', 1),
(1, 'Informações sobre Preços', 'Cliente quer saber sobre valores', 'preco,valor,quanto,custa,orcamento', 'Entendo que você gostaria de saber sobre nossos valores! 💰 Nossos preços variam dependendo do tratamento. Para te dar um orçamento mais preciso, que tipo de procedimento você tem interesse?', 'send_info', 1),
(1, 'Emergência Odontológica', 'Cliente tem urgência/dor', 'dor,urgencia,emergencia,socorro,doi', 'Sinto muito que você esteja sentindo dor! 🆘 Entendo que é uma situação urgente. Temos horários de emergência disponíveis. Você pode vir hoje? Vou verificar nossa agenda imediatamente.', 'transfer_to_agent', 1),
(1, 'Informações Gerais', 'Cliente quer informações sobre a clínica', 'clinica,consultorio,endereco,telefone,funcionamento', 'Olá! Será um prazer te contar mais sobre nossa clínica! 🏥 Somos especializados em odontologia completa com equipamentos modernos. Funcionamos de segunda a sexta das 8h às 18h. Em que posso ajudar especificamente?', 'send_info', 1),
(1, 'Cancelamento/Reagendamento', 'Cliente quer cancelar ou reagendar', 'cancelar,reagendar,remarcar,mudar,alterar', 'Sem problemas! Entendo que às vezes precisamos ajustar os horários. 📅 Você gostaria de cancelar ou reagendar sua consulta? Se for reagendar, que período seria melhor para você?', 'schedule_appointment', 1);
